import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types'
import * as LeituraAPI from './LeituraAPI'
import Button from '@material-ui/core/Button';
import Post from './Post';
import './App.css';

class Home extends Component {
  state = {
    categorySelect: '',
  }
  onClick = async (a) => {
    const ab = await LeituraAPI.getPostsForCategories(a)
    this.setState({ categorySelect: ab })
    console.log('categorySelect',this.state.categorySelect)
  }

  render() {
    const { posts, categories } = this.props;
    const { categorySelect } = this.state;
    
    gposts.filter(a => a.category === categorySelect) ? console.log("cheio") : console.log("vazio")
    
    return (

      <div>
        <div className="home">
          <h1><strong>Leitura</strong></h1>
        </div>

        <div className="grid-posts">
          <div className="grid-posts-item1">
            {
              categorySelect !== '' ?
                posts.filter(a => a.category === categorySelect).map(post => (
                  <Post
                    key={post.id}
                    id={post.id}
                    title={post.title}
                    category={post.category}
                    author={post.author}
                    timestamp={post.timestamp}
                    commentCount={post.commentCount}
                    body={post.body}
                    voteScore={post.voteScore}
                  />
                ))
                  :
                posts.map(post => (
                  <Post
                    key={post.id}
                    id={post.id}
                    title={post.title}
                    category={post.category}
                    author={post.author}
                    timestamp={post.timestamp}
                    commentCount={post.commentCount}
                    body={post.body}
                    voteScore={post.voteScore}
                  />
                ))
            }
          </div>

          <div className="grid-posts-item2">
            <div className="meconheca">
              <Link to="/update"><div className="botaoEnviar">NEW POST</div></Link>
            </div>

            <div className="categories">
              <h1>SORT BY </h1>
              <select>
                <option value="volvo">Date</option>
                <option value="saab">Title</option>
                <option value="opel">Author</option>
                <option value="audi">Comments</option>
                <option value="audi">More votes</option>
                <option value="audi">Less votes</option>
              </select>
            </div>

            <div className="atualizacoes">
              <h1>CATEGORIES</h1>

              {

                <ul>


                  <li>
                    <Button
                      onClick={() => { this.onClick('react'); }}
                    >
                      React
                    </Button>
                  </li>
                  <li>redux</li>
                  <li>all</li>
                </ul>


              }





            </div>

          </div>
        </div>
      </div>
    );
  }
}

Home.propTypes = {
  posts: PropTypes.array.isRequired,
  //categories: PropTypes.string.isRequired,
};

export default Home;
