const api = "http://localhost:3001";

const headers ={
    'Accept': 'application/json',
    'Authorization': 'whatever-you-want'
}

export const getAllPosts = () =>
    fetch(`${api}/posts`, {headers})
    .then(res => res.json())
    .then(data => console.log('data',data.posts))
    .catch(error => console.log(error))



export default api;
