import { getAllPosts, getPostDetails, getPostComments, createPost } from '../LeituraAPI'
import { receivePosts, receivePostDetails, receivePostComments, newPost } from './posts'

let genereteId = localStorage.token
if (!token)
genereteId = localStorage.token = Math.random().toString(36).substr(-8)

export function handleInitialData() {
    return (dispatch) => {
        return getAllPosts()
            .then((posts) => {
                dispatch(receivePosts(posts))
            })
    }
}


export function postDetails(id) {
    return (dispatch) => {
        return getPostDetails(id)
            .then(post => {
                dispatch(receivePostDetails(post))
            })
    }
}

export function postComments(id) {
    return (dispatch) => {
        return getPostComments(id)
            .then(comments =>{
                dispatch(receivePostComments(comments))
            })
    }
}

export function addPost(newPostContent){
    const post ={
        id: genereteId,
        timestamp: newPostContent.timestamp,
        title: newPostContent.title,
        body: newPostContent.body,
        author: newPostContent.author.body,
        category: newPostContent.category,
    }
    return dispatch =>{
        return createPost(post)
        .then(resp => dispatch(newPost(resp)))
    }
    
}
