import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem'
import { withStyles } from '@material-ui/core/styles';
import './App.css';

const styles = theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
    },
    dense: {
        marginTop: 19,
    },
    menu: {
        width: 200,
    },
});

const categoriesList = [
    {
        id: 0,
        title: 'React',
        category: 'react',
    },
    {
        id: 1,
        title: 'Redux',
        category: 'redux',
    },
    {
        id: 2,
        title: 'Udacity',
        category: 'udacity',
    },
];

class UpdatePost extends Component {

    constructor() {
        super();
        this.state = {
            isExpandedCustomsTransitDeclaration: false,
            errors: {
                issueDate: false,
                trafficCompletionDate: false,
                customsEnclosure: false,
                number: false,
            },
            values: {
                id: 0,
                issueDate: null,
                trafficCompletionDate: null,
                customsEnclosure: '',
                number: '',
            },
        };
    }


    render() {

        const { classes } = this.props;
        return (
            <div>
                <div className="home">
                    <h1><strong>Leitura</strong></h1>
                </div>
                <div className="container-view">
                    <div className="item-main">
                        <div className="formulario">
                            <h1>Create new post</h1>
                            <form className={classes.container} noValidate autoComplete="off">
                                <TextField
                                    required
                                    id="title"
                                    //value={this.state.values.customsEnclosure || ''}
                                    autoFocus
                                    placeholder="Title"
                                    label="Title"
                                    type="text"
                                    // onChange={this.handleChangeCustomsTransitDeclaration}
                                    margin="normal"
                                    fullWidth

                                //error={fieldReturnError(errors.customsEnclosure)}
                                //helperText={errors.customsEnclosure ? 'Customs Enclosure required' : ''}
                                />
                                <TextField
                                    required
                                    id="category"
                                    select
                                    label="Category"
                                    SelectProps={{
                                        MenuProps: {
                                            className: classes.menu,
                                        },
                                    }}
                                    helperText="Please select your category"
                                    margin="normal"
                                    fullWidth
                                >
                                    {categoriesList.map(option => {
                                        /* jshint expr: true */
                                        <MenuItem
                                            key={option.id}
                                            value={option.title}
                                      />
                                    })}
                                </TextField>

                                <input
                                    id="nome"
                                    name="nome"
                                    type="text"
                                    placeholder="Categorie"
                                    required
                                />
                                <input
                                    id="nome"
                                    name="nome"
                                    type="text"
                                    placeholder="Author"
                                    required
                                />

                                <textarea
                                    name="mensagem"
                                    id="mensagem"
                                    rows="5"
                                    placeholder="Body"
                                    required>
                                </textarea>

                                <input
                                    type="submit"
                                    name="enviar"
                                    value="PUBLISH"
                                    className="botaoEnviar" />
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default withStyles(styles)(UpdatePost);
