import { getAllPosts, getPostDetails } from '../LeituraAPI'
import { receivePosts, postDetails } from './posts'

export function handleInitialData() {
    return (dispatch) => {
        return getAllPosts()
            .then((posts)  => {
            dispatch(receivePosts(posts))
        })
    }
}


export function postDetais(){
    return (dispatch) =>{
        return getPostDetails(dispatch)
            .then(post =>{
                dispatch(postDetails(post))
            })
    }
}
