import React, { Component } from 'react'
import TextField from '@material-ui/core/TextField'
import MenuItem from '@material-ui/core/MenuItem'
import Button from '@material-ui/core/Button'
import { withStyles } from '@material-ui/core/styles'
import { addPost } from './actions/shared'

import './App.css';

const styles = theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
    },
    dense: {
        marginTop: 19,
    },
    menu: {
        width: 200,
    },
});

const categoriesList = [
    {
        id: 0,
        title: 'React',
        category: 'react',
    },
    {
        id: 1,
        title: 'Redux',
        category: 'redux',
    },
    {
        id: 2,
        title: 'Udacity',
        category: 'udacity',
    },
];

class UpdatePost extends Component {

    constructor() {
        super();
        this.state = {
            timestamp: Date.now(),
            title: '',
            body: '',
            author: '',
            category: '',
        };
    }

    handleChange = name => event => {
        this.setState({ [name]: event.target.value });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        const { dispatch } = this.props
        const {post} = this.state
        dispatch(addPost(post))

        this.setState(() => ({
            post: '',
        }))
    }


    render() {

        const { classes } = this.props;
        const { title, category, body, author } = this.state

        return (
            <div>
                <div className="home">
                    <h1><strong>Leitura</strong></h1>
                </div>
                <div className="container-view">
                    <div className="item-main">
                        <div className="formulario">
                            <h1>Create new post</h1>
                            <form
                                className={classes.container}
                                noValidate
                                autoComplete="off"
                                onSubmit={e => this.handleSubmit(e)}
                            >
                                <TextField
                                    required
                                    id="title"
                                    value={title || ''}
                                    placeholder="Title"
                                    label="Title"
                                    type="text"
                                    onChange={this.handleChange('title')}
                                    margin="normal"
                                    fullWidth
                                    helperText="Input the title of post"
                                />
                                <TextField
                                    required
                                    id="category"
                                    select
                                    label="Category"
                                    value={category}
                                    onChange={this.handleChange('category')}
                                    SelectProps={{
                                        MenuProps: {
                                            className: classes.menu,
                                        },
                                    }}
                                    helperText="Please select your category"
                                    margin="normal"
                                    fullWidth
                                >
                                    {categoriesList.map(option => (
                                        <MenuItem
                                            key={option.id}
                                            value={option.title}
                                        >
                                            {option.title}
                                        </MenuItem>
                                    ))}
                                </TextField>

                                <TextField
                                    required
                                    id="author"
                                    value={author || ''}
                                    placeholder="Author"
                                    label="Author"
                                    type="text"
                                    onChange={this.handleChange('author')}
                                    margin="normal"
                                    fullWidth
                                    helperText="Input the author of post"
                                />

                                <TextField
                                    required
                                    id="body"
                                    value={body || ''}
                                    label="Body"
                                    type="text"
                                    onChange={this.handleChange('body')}
                                    multiline
                                    rowsMax="5"
                                    margin="normal"
                                    fullWidth
                                />
                                <Button
                                    variant="contained"
                                    color="secondary"
                                    type="submit"
                                >
                                    Publish
                                </Button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default withStyles(styles)(UpdatePost);
