import { getAllPosts } from '../LeituraAPI'
import { receivePosts, postDetails } from './posts'

export function handleInitialData() {
    return (dispatch) => {
        return getAllPosts()
            .then((posts)  => {
            dispatch(receivePosts(posts))
        })
        return getPostDetails()
            .then((id))
    }
}
