import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Home from './Home';
import ViewPost from './ViewPost';
import UpdatePost from './UpdatePost';
import { Route } from  'react-router-dom';
import * as LeituraAPI from './LeituraAPI'



class App extends Component {

  state = {//colocamos no state para que o React gerencie as atualizações 
    book: [],
  }
componentDidMount() {//deixa de forma assincrona
    const books = LeituraAPI.getAllPosts()//fala para esperar até pegar todos os dados
    console.log('dan',books)
  }
  render() {
    console.log('book', this.state.book)
    return (
      <div>
      <Route exact path="/" render={() =>(
        <Home />
      )}/>

      <Route path="/post" render={() =>(
        <ViewPost />
      )}/>


      <Route path="/update" render={() =>(
        <UpdatePost />
      )}/>
      </div>
    );
  }
}

export default App;
